def frequence_bytes(liste_bytes):
  nb_bytes = len(liste_bytes)
  dictionnaire = {}

  for byte in liste_bytes:
    if byte in dictionnaire:
      dictionnaire[byte] += 1
    else:
      dictionnaire[byte] = 1

  for cle in dictionnaire:
    dictionnaire[cle] = round(dictionnaire[cle] / nb_bytes * 100 , 1) # todo à enlever round

  return dictionnaire











